      {/* Quick Inventory Display - Hidden on mobile */}
      {!isMobile && (
        <div style={{
          position: 'absolute',
          top: '200px',
          left: '20px',
          ...panelStyle,
          maxHeight: '250px',
          overflowY: 'auto'
        }}>
        <h4 style={{ margin: '0 0 10px 0' }}>Inventory:</h4>
        <div style={{ maxHeight: '120px', overflowY: 'auto' }}>
